	public static int[] reverse(int[] data) {
		int[] reversedData = new int[data.length];
		int j = data.length;
		for(int i =0; i<data.length;i++) {
			reversedData[j-1] = data[i] * -1;
			j = j- 1;
		}
		return reversedData;
	}
	
